targets.query:
from(bucket: "finance_data")
  // 1. Bind to the Dashboard Time Picker
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "market_price")
  |> filter(fn: (r) => r["symbol"] == "GC=F")
  |> filter(fn: (r) => r["_field"] == "price")
  
  // 2. Sort by time to ensure we get the true chronological Start and End
  |> sort(columns: ["_time"], desc: false)
  
  // 3. Find the Start Price and End Price in one pass
  |> reduce(
      fn: (r, accumulator) => ({
          start_price: if accumulator.count == 0 then r._value else accumulator.start_price,
          end_price: r._value,
          count: accumulator.count + 1
      }),
      identity: {start_price: 0.0, end_price: 0.0, count: 0}
  )
  
  // 4. Calculate the Percentage Change: ((End - Start) / Start) * 100
  |> map(fn: (r) => ({ 
      _value: if r.start_price == 0.0 then 0.0 else (r.end_price - r.start_price) / r.start_price * 100.0 
  }))
  |> yield(name: "Period Change")

