from(bucket: "finance_data")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "market_price")
  // Filter for both 24K and 22K symbols
  |> filter(fn: (r) => r["symbol"] == "GOLD_GRAM_24K" or r["symbol"] == "GOLD_GRAM_22K")
  |> filter(fn: (r) => r["_field"] == "price")
  // Group by 1 Day (1d) and take the 'last' value recorded that day
  |> aggregateWindow(every: 1d, fn: last, createEmpty: false)
  |> yield(name: "Gold Daily Close")

from(bucket: "finance_data")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "market_price")
  // Filter for both Fine and Sterling Silver
  |> filter(fn: (r) => r["symbol"] == "SILVER_GRAM_999" or r["symbol"] == "SILVER_GRAM_925")
  |> filter(fn: (r) => r["_field"] == "price")
  // Group by 1 Day (1d) and take the 'last' value
  |> aggregateWindow(every: 1d, fn: last, createEmpty: false)
  |> yield(name: "Silver Daily Close")
